const getRandomWords = function(count = 1, wordsArray = random_words){    
    let indexList = [];
    for(let i =0; i<count; i++){
        let index = parseInt(Math.random() * random_words.length);
        if(indexList.indexOf(index) == -1)
        {
            indexList.push(index);
        }
    }
    return indexList.map(function(i){return random_words[i]});
};

const getRandomNouns = (count = 1) => getRandomWords(count, random_nouns);
const getRandomVerbs = (count = 1) => getRandomWords(count, random_verbs);
const getRandomAdjectives = (count = 1) => getRandomWords(count, random_adjectives);
const getRandomPlurals = (count = 1) => getRandomWords(count, random_plurals);

const getFinalList = function(){
    let result = [...getRandomNouns(), ...getRandomVerbs(), ...getRandomAdjectives(), ...getRandomPlurals()];    
    return result;
}

